"use strict";

var slice = require('./slice');

/**
 * Returns all but the last element of the given list or string.
 *
 * @param {Array} arr
 * @return {*}
 * @example
 *
 *      init([1, 2, 3]);  //=> [1, 2]
 *      init([1, 2]);     //=> [1]
 *      init([1]);        //=> []
 *      init([]);         //=> []
 */
module.exports = function (arr) {
  return slice(0, -1, arr);
};