interface Tail {
    (list: string): string;
    <T>(list: ArrayLike<T>): T[];
}
declare const _default: Tail;
/**
 * Returns all but the first element of the given array.
 *
 * @param {Array} arr
 * @return {*}
 * @example
 *
 *      tail(['fi', 'fo', 'fum']); //=> ['fo', 'fum']
 *      tail([]); //=> []
 */
export default _default;
