interface ToArray {
    <T>(v: ArrayLike<T>): T[];
    <T>(v: T): T extends Array<any> ? T : T[];
}
declare const _default: ToArray;
/**
 * Converts val to array. If val is array return it.
 * @param {*} val
 * @return {Array}
 * @example
 *
 *      toArray([1,2,3]) // => [1,2,3]
 *      toArray(5) // => [5]
 *      toArray('test') // => ['test']
 */
export default _default;
