declare const _default: (test: any) => boolean;
/**
 * Checks if `test` is empty.
 *
 * @param {*} test The value to check.
 * @returns {boolean} Returns `true` if `test` is empty, else `false`.
 * @example
 *
 * isEmpty([]); // => true
 * isEmpty(null); // => true
 * isEmpty({}); // => true
 * isEmpty(''); // => true
 * isEmpty('test'); // => false
 * isEmpty({ a: 5 }); // => false
 * isEmpty([1]); // => false
 */
export default _default;
