"use strict";

var isNil = require('./nil');

var isArray = require('./array');

var isString = require('./string');

var isObject = require('./object');

/**
 * Checks if `test` is empty.
 *
 * @param {*} test The value to check.
 * @returns {boolean} Returns `true` if `test` is empty, else `false`.
 * @example
 *
 * isEmpty([]); // => true
 * isEmpty(null); // => true
 * isEmpty({}); // => true
 * isEmpty(''); // => true
 * isEmpty('test'); // => false
 * isEmpty({ a: 5 }); // => false
 * isEmpty([1]); // => false
 */
module.exports = function (test) {
  switch (true) {
    case isNil(test):
      return true;

    case isString(test):
      return test === '';

    case isArray(test):
      return test.length === 0;

    case isObject(test):
      return Object.keys(test).length === 0;
  }

  return false;
};