interface IsTrue {
    (val: true | 'true'): true;
    <T extends boolean>(val: T): T;
    (val: any): false;
}
declare const _default: IsTrue;
/**
 * Returns true if `val` is true or equal to string 'true'
 *
 * @param val
 * @returns {boolean}
 *
 * @example
 *
 *      isTrue(true) // => true
 *      isTrue('true') // => true
 *      isTrue([true]) // => false
 *      isTrue('false') // => false
 */
export default _default;
